                // EditView.activityMessages.data_messages = data.messages;
                AjaxObj
                    .createInstance()
                    .setUrl(this._url + '/' + $(this._element).closest('.edit-view').data('copy_id'))
                    .setData(this._data)
                    .setType('POST')
