                begOperator = ProcessObj.getParams(bpmOperators.filter('[data-unique_index="'+path.arrBegin+'"]'));
                endOperator = ProcessObj.getParams(bpmOperators.filter('[data-unique_index="'+path.arrEnd+'"]'));

